import React, { Component } from 'react';
import './App.css';
// 파일을 분리해주면 import해줘야 한다.
import Subject from './Components/Subject';
import TOC from './Components/TOC';
import Control from './Components/Control';
import ReadContent from './Components/ReadContent';
import CreateContent from './Components/CreateContent';

class App extends Component {
  // props를 사용하기 위한 코드
  // 내장 객체를 사용하여 state 값을 새로운 값으로 초기화 하기 위해 사용한다. -> 정도로만 이해함 -> 자세한 공부 필요
  constructor(props) { // 클래스 안의 function은 'function' 키워드를 생략할 수 있다.
    super(props); 
    this.max_content_id = 3;
    this.state = { // state 값을 새로운 값으로 초기화
      mode: "read",
      selected_content_id: 2,
      subject: {title: "Web", sub: "World Wide Web!"},
      welcome: {title: "Welcome", desc: "Hello, React!!"}, // mode가 welcome일때 실행
      contents: [
        {id: 1, title: "HTML", desc:"HTML is Information"},
        {id: 2, title: "CSS", desc:"CSS is for design"},
        {id: 3, title: "JavaScript", desc:"JavaScript is for interactive"}
      ]
    };
  };

  // 위의 state 내용이 바뀔때마다 render()가 다시 실행됨.
  render() {
    console.log("App render");
    let _title, _desc, _article = null;

    // mode 판별해주는 분기문
    if(this.state.mode === "welcome") {
      _title = this.state.welcome.title;
      _desc = this.state.welcome.desc;
      _article = <ReadContent title={_title} desc={_desc} />;
    } else if(this.state.mode === "read") {
      for(let i=0; i<this.state.contents.length; i++){
        let data = this.state.contents[i];
        if(data.id === this.state.selected_content_id) {
          _title = data.title;
          _desc = data.desc;
          break;
        }  
      }
      // 동시에 밑의 {article} 영역이 바뀌게 해주는 코드
      _article = <ReadContent title={_title} desc={_desc} />;
    } else if(this.state.mode === "create") {
      _article = <CreateContent onSubmit={function(_title, _desc) {
        // setState를 통해서 새로운 컨텐츠를 추가하기
        this.max_content_id = this.max_content_id + 1;
        this.state.contents.push({
          id:this.max_content_id,
          title: _title,
          desc: _desc
        });
        this.setState({
          contents: this.state.contents
        });
      }.bind(this)}></CreateContent>
    } 

    // 하나의 최상위 태그만 사용해야 한다.
    return(
      <div>
        <Subject 
          title={this.state.subject.title} 
          sub={this.state.subject.sub}
          onChangePage={function(){
            // 기존코드 => this.state.mode = 'welcome';
            // 이걸 추가 안하면 반응 없음
            // 페이지를 바꾸기 위해 setState값이 바뀌었다는 걸 알려준다.
            this.setState({mode: "welcome"}); // 변경하고 싶은 값을 객체 형태로 알려준다.
          }.bind(this)}
        />
        <TOC 
          data={this.state.contents} 
          onChangePage={function(id){
            this.setState({
              mode: "read",
              selected_content_id: Number(id) // id값과 일치하는 content를 출력하기
            });
          }.bind(this)}
        />
        <Control 
          onChangeMode={function(_mode){
            this.setState({
              mode: _mode
            });
          }.bind(this)}
        />
        {_article}
      </div>
    );
  }
}

export default App;
