import React, { Component } from "react";

class TOC extends Component {
  render() {
    console.log("TOC remder");
    //this.props.data = 'h1'; // 컴포넌트 안의 props 자신을 바꾸는 것은 안된다. (대신 밖에서는 가능 -> state를 이용하여)
    let data = this.props.data;

    let lists = [];
    for(let i=0; i<data.length; i++){
      // 반복하는 경우 각각의 목록을 구별할 수 있는 식별자를 적어줘야 한다. (key)
      lists.push(
        <li key={data[i].id}>
          <a href={`/content/${data[i].id}`} 
            //data-id={data[i].id} // 형식으로 확인할 수 있다. 또한 이 속성을 사용하기 싫은 경우 bind 안에 값으로 넣어준다.
            onClick={
              // app.js에서 onChangePage 이벤트를 추가해주면 아래의 함수를 원본에 추가해줘야 한다.
              function(id, e){ 
                // 이때 id값은 bind함수에 id를 두 번쨰 인자에 넣었기 때문에 함수의 첫 번째 매개변수 값으로 넣어줘야 한다.(기존의 값은 뒤로 한 칸씩 밀림)
                e.preventDefault(); // 링크를 클릭했을 때  페이지가 바뀌는 것을 방지
                this.props.onChangePage(id); // 클릭 이벤트 발생시키기 위한 함수 호출
              }.bind(this, data[i].id)}>
            {data[i].title}</a>
        </li>
      );
    }

    return(
      <nav>
        <ul>
          {lists}
        </ul>
      </nav>
    );
  }
}

export default TOC;
