import React, { Component } from "react";
import axios from "axios";
import PageTitle from "./PageTitle";
import BoardList from "./BoardList";
import BoardRead from "./BoardRead";
import BoardCreate from "./BoardCreate";
import BoardUpdate from "./BoardUpdate";

class Article extends Component {
  constructor(props) {
    super(props);
    this.state = {
      mode: "list",
      selectBoardNo: 0,
    };
    this.setModeAndNo = this.setModeAndNo.bind(this);
  }

  deleteBoard() {
    axios.post("http://localhost:8000/delete", {
      no: this.state.selectBoardNo
    })
      .then(function(response){
      }.bind(this))
      .catch(function(error){
        console.error(error);
      });
  }

  setModeAndNo(_mode, no=0){
    if(_mode === "delete") {
      Swal.fire({
        title: '정말로 그렇게 하시겠습니까?',
        text: "다시 되돌릴 수 없습니다. 신중하세요.",
        icon: 'warning',
        showCancelButton: true,
        confirmButtonColor: '#3085d6',
        cancelButtonColor: '#d33',
        confirmButtonText: '승인',
        cancelButtonText: '취소',
        reverseButtons: true, // 버튼 순서 거꾸로
        
      }).then((result) => {
        if (result.isConfirmed) {
          this.deleteBoard();
          this.setState({
            mode: "list"
          });
          Swal.fire(
            '승인이 완료되었습니다.',
            '화끈하시네요~!',
            'success',
            
          )
        } else {
          this.setState({
            mode: "read",
            selectBoardNo: no
          });
        }
      })
      
      // if(window.confirm("정말로 삭제하겠습니까?")) {
      //   this.deleteBoard();
        
      //   this.setState({
      //     mode: "list"
      //   });
      //   alert("삭제 되었습니다.");
      // } else {
      //   this.setState({
      //     mode: "read",
      //     selectBoardNo: no
      //   });
      // }
    } else {
      this.setState({
        mode: _mode,
        selectBoardNo: no
      });
    }
  }

  getContent() {
    let mode = this.state.mode;
    let section = null;

    if(mode === "list") {
      section = <BoardList boards={this.state.freeBoard} onSelectItem={this.setModeAndNo} />;
    } else if(mode === "read") {
      section = <BoardRead no={this.state.selectBoardNo} onButtonClick={this.setModeAndNo} />;
    } else if(mode == "create") {
      section = <BoardCreate onCreate={this.setModeAndNo}/>
    } else if(mode === "update") {
      section = <BoardUpdate no={this.state.selectBoardNo} onButtonClick={this.setModeAndNo}/>
    }

    return section;
  }

  render() {
    return (
      <article>
        <PageTitle />
        {this.getContent()}
      </article>
    );
  }
}

export default Article;
